import React, { Component } from 'react';
import styled from 'styled-components';
import emojione from 'emojione';

class DisplayConversation extends Component {
  constructor() {
    super();
    this.state = {
      chat: [
        {
          senderId: "you",
          text: "testing testing testing :face_with_head_bandage: ",
        },
        {
          senderId: "other",
          text: "testing testing testing :face_with_head_bandage: ",
        },
        {
          senderId: "other",
          text: "testing testing  :face_with_head_bandage: :face_with_head_bandage: ",
        },
      ],
    };
  }


  componentDidMount() {
    this.scrollToBottomLoad();
  }

  componentDidUpdate() {
    this.scrollToBottom();
  }

  emojify(emoji) {
    var m;
    var emojy = emoji
    var regex = /(?:\:)\b(\w*)\b(?=\:)/g;
    var arr = []
    while (m = regex.exec(emojy)) arr.push(m[1]);
    var emojify = emojy.replace(regex, <Emoji
      className='ld-emoji'
      role='presentation'
      dangerouslySetInnerHTML={{ __html: emojione.unicodeToImage(arr.parse()) }} />);
    console.log(emojify);

  }

  scrollToBottom() {
    this.el.scrollIntoView({ behavior: 'smooth' });
  }

  scrollToBottomLoad() {
    this.el.scrollIntoView();
  }
  render() {
    return (
      <div className="displayConversation" >
        <ul >
          {this.state.chat.map((message, index) => (
            <li key={index} className={`message ${message.senderId}`}>
              <p className="message-text one" >{this.emojify(message.text)}</p>
            </li>
          ))}
          <div ref={el => { this.el = el; }} />
        </ul>
      </div>
    )
  }
}

const Emoji = styled.span`
  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0.21rem;
  img {
    height: 16px !important;
    width: 16px !important;
  }
`
export default DisplayConversation;
