import React, { useEffect } from 'react';
import emojione from 'emojione';
import $ from "jquery";
import Message from './message';

export default function Chat(props) {
    const chatText = props.chat;
    const user = props.user;
    let el = React.createRef();
    const [onClickedMessage, setClickedMessage] = React.useState(0);
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);

    function handleClick(e) {
        setOpen(prev => !prev);
        setClickedMessage(e);
        console.log(onClickedMessage);
    };

    const handleClickAway = () => {
        setOpen(false);
    };

    useEffect(() => {
        $(document).ready(function () {
            $(".message-text").each(function () {
                var original = $(this).html();
                var converted = emojione.toImage(original);
                $(this).html(converted);
            });
        });
        el.current.scrollIntoView();
    })

    function userSort(value) {
        if (value === user) {
            return ('you');
        } else {
            return ('other');
        }
    }

    return (
        <div className="displayConversation" >
            <ul >
                {chatText.map((message, index) => (
                    <Message
                        senderId={message.senderId}
                        text={message.text}
                    />
                ))}
                <div ref={el} />
            </ul>
        </div >
    )

}
