import React from 'react';
import Picker from 'react-emojipicker'
import { element } from 'prop-types';

class Login extends React.Component {
    constructor() {
        super();
        this.state = {
            commandHistory: [],
            terminalOutput: [],
            value: '',
        };
        this.handleTextChange = this.handleTextChange.bind(this);
        this.keyPress = this.keyPress.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.setWrapperRef = this.setWrapperRef.bind(this);
        this.handleClickOutside = this.handleClickOutside.bind(this);
    }

    componentDidMount() {
        document.addEventListener('mousedown', this.handleClickOutside);
    }

    componentWillUnmount() {
        document.removeEventListener('mousedown', this.handleClickOutside);
    }

    setWrapperRef(node) {
        this.wrapperRef = node;
    }

    handleTextChange(e) {
        this.setState({
            value: e.target.value
        });
    }

    handleChange() {
        if (this.state.revealLogin === false) {
            this.setState({ revealLogin: true });
        } else {
            this.setState({ revealLogin: true });
        }
    }

    handleChanges() {
        this.setState({ revealLogin: false })
    }

    handleClickOutside(event) {
        if (this.wrapperRef && !this.wrapperRef.contains(event.target) && this.state.revealLogin === true) {
            this.setState({ revealLogin: false });
        }
    }

    logEmoji(emoji) {
        const val = this.state.value + " " + emoji.shortname + " ";
        console.log(val)
    }
    keyPress(e) {
        if (e.keyCode === 13) {
            console.log(e.target.value);
            var newArray = this.state.commandHistory;
            newArray.push(e.target.value);
            this.setState({
                commandHistory: newArray,
                terminalOutput: [e.target.value, ...this.state.terminalOutput],
                value: ''
            });
            console.log(this.state.commandHistory);
        }
    }

    render() {
        const style = this.state.revealLogin ? {} : { display: 'none' };

        return (
            <div className="messagingSend">
                <div id="send">
                    <input value={this.state.value} onKeyDown={this.keyPress} onChange={this.handleTextChange} id="terminalInput" ></input>
                    <div className="submit"><span>Send</span></div>
                    <div className="emojiButton" ><img onClick={this.handleChange} src="./icons/icons_alien.png" className="emojiButton" /> </div>
                </div>
                <div className="emojiBox123" style={style} ref={this.setWrapperRef} >
                    <Picker onEmojiSelected={this.logEmoji.bind(this)} />
                    <div className="closeBottom" onClick={this.handleChanges.bind(this)}></div>
                </div>
            </div>
        )
    }
}
export default Login;
