import React, { useEffect } from 'react';
import emojione from 'emojione';
import $ from "jquery";
import ClickAwayListener from '@material-ui/core/ClickAwayListener';
import clsx from 'clsx';

import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    wrapper: {
        position: 'relative',
    },
    paper: {
        padding: theme.spacing(1),
        backgroundColor: theme.palette.background.paper,
    },
}));

export default function Chat(props) {
    const chatText = props.chat;
    const user = props.user;
    let el = React.createRef();
    const [onClickedMessage, setClickedMessage] = React.useState(0);
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);

    function handleClick(e) {
        setOpen(prev => !prev);
        setClickedMessage(e);
        console.log(onClickedMessage);
    };

    const handleClickAway = () => {
        setOpen(false);
    };

    useEffect(() => {
        $(document).ready(function () {
            $(".message-text").each(function () {
                var original = $(this).html();
                var converted = emojione.toImage(original);
                $(this).html(converted);
            });
        });
        el.current.scrollIntoView();
    })

    function userSort(value) {
        if (value === user) {
            return ('you');
        } else {
            return ('other');
        }
    }

    return (
        <div className="displayConversation" >
            <ul >
                {chatText.map((message, index) => (
                    <li key={index} className={`message ${userSort(message.senderId)}`}
                    >
                        <p className="message-text one" >{message.text}</p>
                        <div><span onClick={function handleClick() {
                            setOpen(prev => !prev);
                        }}>. . .</span>
                            {open ? <div className={classes.paper}>Click me, I will stay visible.</div> : null}
                        </div>
                    </li>
                ))}
                <div ref={el} />
            </ul>
        </div >
    )

}
