import React from 'react';
import Chat from './DEMO-CHAT.json';

export default function messenger(props) {
    const user = props.name;
    const chatText = Chat.chat

    return (
        <div className="messenger">
            <div className="displayConversation" >
                <ul >
                    {chatText.map((message, index) => (
                        <li key={index} className={`message ${message.senderId}`}>
                            <p className="message-text one" >{message.text}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    )
}
