  },
  {
    senderId: 'you',
    text: 'I like trains'
  },
  {
    senderId: 'you',
    text: 'This is the last message'
  }
]
function scrollToBottom() {
  var objDiv = document.getElementById("display");
  objDiv.scrollTop = objDiv.scrollHeight;
}

class DisplayConversation extends Component {
  render() {
    return (
      <div id="display" className="displayConversation" >
        <ul >
          {DUMMY_DATA.map((message, index) => (
            <li key={index} className={`message ${message.senderId}`}>
              <p className="message-text one" >{message.text}</p>
            </li>
          ))}
          <li onLoad={scrollToBottom}></li>
        </ul>
      </div>
    )
  }
}
export default DisplayConversation;
