import React from 'react';
import EmojiPicker from 'emoji-picker-react';

class Login extends React.Component {
    constructor() {
        super();
        this.state = {
        };

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        if (this.state.hideLogin === true) {
            this.setState({ hideLogin: false });
        } else {
            this.setState({ hideLogin: true });
        }
    }
    logEmoji(emoji) {
        console.log(emoji)
    }

    render() {
        const style = this.state.hideLogin ? { width: '300px' } : { display: 'none' };

        return (
            <div className="messagingSend">
                <div id="send">
                    <textarea className="sendInput"></textarea>
                    <div className="submit"><span>Send</span></div>
                    <div className="emojiButton" ><img onClick={this.handleChange} src="./icons/icons_alien.png" className="emojiButton" /> </div>
                </div>
                <div className="emojiBox123" style={style} >
                    <EmojiPicker onEmojiClick={this.logEmoji.bind(this)} />
                </div>
            </div>
        )
    }
}
export default Login;
