import React, { useEffect, useState, useRef } from "react";
import Button from '@material-ui/core/Button';

const Message = ({ senderId, text, messageNumber }) => {
    const node = useRef();
    const [open, setOpen] = useState(false);
    const [remove, setRemove] = useState(false);

    const handleClick = e => {
        if (node.current.contains(e.target)) {
            // inside click
            return;
        }
        // outside click
        setOpen(false);
    };

    //const handleChange = selectedValue => {
    //    onChange(selectedValue);
    //   setOpen(false);
    //};

    useEffect(() => {
        document.addEventListener("mousedown", handleClick);

        return () => {
            document.removeEventListener("mousedown", handleClick);
        };
    }, []);

    function checkDelete() {
        if (remove === true) {
            return 'hidden'
        } else {
            return ' '
        }
    }

    return (
        <li className={`message ` + senderId} id={messageNumber + 'message'}>
            <p className="message-text one" >{text}</p>
            <div ref={node} className="dropdown">
                <span onClick={e => setOpen(!open)}>. . .</span>
                {open && (
                    <div className="dropdown-menu">
                        <Button
                            color="secondary"
                            className="dropdown-item"
                            size="small"
                            onDoubleClick={() => {
                                if (remove === false) {
                                    setRemove(true);
                                }
                            }}
                        >Delete</Button>
                        <Button className="dropdown-item" color="primary" size="small">Edit</Button>
                    </div>
                )}
            </div>
        </li>
    );
};

export default Message;
