import React, { useEffect } from 'react';
import emojione from 'emojione';
import $ from "jquery";
import ClickAwayListener from '@material-ui/core/ClickAwayListener';

import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    wrapper: {
        position: 'relative',
    },
    paper: {
        position: 'absolute',
        top: 28,
        right: 0,
        left: 0,
        border: '1px solid',
        padding: theme.spacing(1),
        backgroundColor: theme.palette.background.paper,
    },
}));

export default function Chat(props) {
    const chatText = props.chat;
    const user = props.user;
    let el = React.createRef();
    const [onClickedMessage, setClickedMessage] = React.useState(0);
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);

    function handleClick(e) {
        setOpen(prev => !prev);
        setClickedMessage(e);
        console.log(onClickedMessage);
    };

    const handleClickAway = () => {
        setOpen(false);
    };

    useEffect(() => {
        $(document).ready(function () {
            $(".message-text").each(function () {
                var original = $(this).html();
                var converted = emojione.toImage(original);
                $(this).html(converted);
            });
        });
        el.current.scrollIntoView();
    })

    function userSort(value) {
        if (value === user) {
            return ('you');
        } else {
            return ('other');
        }
    }
    function start(e) {
        console.log('START');
    }

    function end(e, enough) {
        console.log('END');
        console.log(enough ? 'Click released after enough time' : 'Click released too soon');
    }

    return (
        <div className="displayConversation" >
            <ul >
                {chatText.map((message, index) => (
                    <ClickAwayListener key={index} onClickAway={handleClickAway}>
                        <li className={classes.wrapper + ` message ${userSort(message.senderId)}`} onClick={function handleClick() {
                            setOpen(prev => !prev);
                            setClickedMessage(index);
                            console.log(onClickedMessage);
                        }}>
                            <p className="message-text one" >{message.text}</p>
                            <div className={() => {
                                if (onClickedMessage === index) {
                                    return (' ')
                                } else {
                                    return ('hidden')
                                }
                            }}>hello</div>
                        </li>
                    </ClickAwayListener>
                ))}
                <div ref={el} />
            </ul>
        </div >
    )

}
