import React, { useEffect } from 'react';
import emojione from 'emojione';
import $ from "jquery";
import ClickNHold from 'react-click-n-hold';

export default function Chat(props) {
    const chatText = props.chat;
    const user = props.user;
    let el = React.createRef();
    const [onClicked, setOnClicked] = React.useState(false)
    const [onClickedMessage, setClickedMessage] = React.useState();

    useEffect(() => {
        $(document).ready(function () {
            $(".message-text").each(function () {
                var original = $(this).html();
                var converted = emojione.toImage(original);
                $(this).html(converted);
            });
        });
        el.current.scrollIntoView();
    })

    function userSort(value) {
        if (value === user) {
            return ('you');
        } else {
            return ('other');
        }
    }
    function start(e) {
        console.log('START');
    }

    function end(e, enough) {
        console.log('END');
        console.log(enough ? 'Click released after enough time' : 'Click released too soon');
    }

    return (
        <div className="displayConversation" >
            <ul >
                {chatText.map((message, index) => (
                    <ClickNHold
                        time={.25} // Time to keep pressing. Default is 2
                        onStart={start} // Start callback
                        onClickNHold={function clickNHold(e) {
                            console.log('CLICK AND HOLD');
                            setOnClicked(true)
                            setClickedMessage(index)
                            console.log(onClickedMessage);
                        }} //Timeout callback
                        onEnd={end} >
                        <li key={index} className={`message ${userSort(message.senderId)}`}>
                            <p className="message-text one" >{message.text}</p>
                            <div className={() => { if (onClickedMessage !== index) { return 'hidden' } else { return '' } }}>Hello</div>
                        </li>
                    </ClickNHold>
                ))}
                <div ref={el} />
            </ul>
        </div>
    )

}
