import React from 'react';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import { makeStyles } from '@material-ui/core/styles';
import SelectUser from './selectUser';
import { createMuiTheme } from '@material-ui/core/styles';
import purple from '@material-ui/core/colors/purple';
import { ThemeProvider } from '@material-ui/styles';

const useStyles = makeStyles(theme => ({
    addRoom: {
        position: 'absolute',
        bottom: 57,
        left: 5,
        zIndex: 10000,
        boxShadow: ' 1px -1px 2px 1px #209cee',
        background: 'white',
    },
    fab: {
        backgroundColor: '209cee',
        position: 'absolute',
        bottom: 57,
        left: 10,
        zIndex: 100001,
    },
}));

const theme = createMuiTheme({
    palette: {
        primary: {
            light: '#85edff',
            main: '#209cee',
            dark: '#2e77d5',
        },
        secondary: {
            main: '#f44336',
        },
    },
});

export default function AddUser() {
    const classes = useStyles();
    const [clicks, addClicks] = React.useState(1)

    function addRoomChange() {
        if (clicks % 2 == 0) {
            return ('Display: block;')
        } else {
            return ('Display: none;')
        }
    }

    return (
        <ThemeProvider theme={theme}>
            <Fab color="primary" aria-label="add" className={classes.fab} onClick={() => {
                addClicks(clicks + 1);
            }}>
                <AddIcon />
            </Fab>
            <span>Add User</span>
            <div className={classes.addRoom} >
                <SelectUser Style={addRoomChange()} />
            </div>
        </ ThemeProvider >
    );
}
