import React, { Component } from 'react';

class DisplayConversation extends Component {
  constructor() {
    super();
    this.state = {
      chat: [
        {
          senderId: "you",
          text: "testing testing testing :face_with_head_bandage: ",
        },
        {
          senderId: "other",
          text: "testing testing testing :face_with_head_bandage: ",
        },
        {
          senderId: "other",
          text: "testing testing" + <Emoji
            className='ld-emoji'
            key={index}
            role='presentation'
            onClick={this.onEmojiSelect.bind(this)}
            dangerouslySetInnerHTML={{ __html: emojione.unicodeToImage(emoji) }} /> + "testing :face_with_head_bandage: ",
        },
      ],
    };
  }


  componentDidMount() {
    this.scrollToBottomLoad();
  }

  componentDidUpdate() {
    this.scrollToBottom();
  }

  scrollToBottom() {
    this.el.scrollIntoView({ behavior: 'smooth' });
  }

  scrollToBottomLoad() {
    this.el.scrollIntoView();
  }
  render() {
    return (
      <div className="displayConversation" >
        <ul >
          {this.state.chat.map((message, index) => (
            <li key={index} className={`message ${message.senderId}`}>
              <p className="message-text one" >{message.text}</p>
            </li>
          ))}
          <div ref={el => { this.el = el; }} />
        </ul>
      </div>
    )
  }
}

const Emoji = styled.span`
  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0.21rem;
  img {
    height: 16px !important;
    width: 16px !important;
  }
`
export default DisplayConversation;
